// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./IAccounts.sol";
import "./IBurnableToken.sol";

contract GrowthEarn {

  address treasury;

  IAccounts accountsContract;
  IBurnableToken stakedToken;
  IERC20 sikaToken;

  bool allowDepositing = true;
  bool allowWithdrawing = true;

  uint256 public minDeposit;
  uint256 public totalLockedAmount;

  struct InvestmentStruct {
    address investor;
    uint256 unlockDate;
    uint256 amount;
    bool unlocked;
  }

  InvestmentStruct[] investments;

  // Don't track empty indices since we're using a mapping 
  // which leaves no empty slots when deletions are performed
  struct InvestorStruct {
    uint256 unlockedAmount;
    uint256[] stakesIndices;
  }

  mapping(address => InvestorStruct) investors; // investor => InvestorStruct

  modifier onlyTreasury {
    require(msg.sender == treasury, "You're not the owner");
    _;
  }

  constructor(address sikaTokenAddress, address stakedTokenAddress) {
    sikaToken = IERC20(sikaTokenAddress);
    stakedToken = IBurnableToken(stakedTokenAddress);
    treasury = msg.sender;
  }

  // IMPORTANT!! This contract must be granted admin role in Accounts.sol
  //             in migration or test script via `Accounts.addAdmin`
  //             It serves to allow using Accounts.toggleIsExcludedFromTransferFee

  function setAccountsContract(address contractAddress) external onlyTreasury {
    accountsContract = IAccounts(contractAddress);
    if (!accountsContract.isExcludedFromTransferFee(address(this))) {
      accountsContract.toggleIsExcludedFromTransferFee(address(this));
    }
  }

  function setMinDeposit(uint256 amount) external onlyTreasury {
    minDeposit = amount;
  }

  function toggleAllowDepositing() external onlyTreasury {
    allowDepositing = !allowDepositing;
  }

  function toggleAllowWithdrawing() external onlyTreasury {
    allowWithdrawing = !allowWithdrawing;
  }

  function getUnlockedAmount() external view returns(uint256) {
    InvestorStruct storage investor = investors[msg.sender];
    return investor.unlockedAmount;
  }

  function getInvestments() external view returns(InvestmentStruct[] memory) {
    InvestorStruct storage investor = investors[msg.sender];
    InvestmentStruct[] memory results = new InvestmentStruct[](investor.stakesIndices.length);

    uint256 j;
    for (uint256 i = 0; i < investor.stakesIndices.length; i++) {
      InvestmentStruct storage anInvestment = investments[investor.stakesIndices[i]];
      results[j++] = anInvestment;
    }

    return results;
  }

  // Process
  // 1. call getInvestmentsLength
  // 2. call disburseEarnings by supplying loop's starting index
  //    each call disburses earnings for ~100 accounts
  function getInvestmentsLength() external onlyTreasury view returns(uint256) {
    return investments.length;
  }

  function disburseEarnings(uint256 startLoopAtIndex) external onlyTreasury {

    uint256 counter;

    // iterate over investments
    // below this loop is a way for those with amount below minimum 
    // deposit to unlock their tokens after the locking period
    for (uint256 i = startLoopAtIndex; i < investments.length; i++) {
      InvestmentStruct storage anInvestment = investments[i];

      if (anInvestment.amount == 0 || anInvestment.amount < minDeposit) {
        continue; // non-existent key or amount below minimum deposit
      }

      // IMPORTANT: Overflow may occur but value will 
      //            be truncated, hence no need to fix it.
      uint256 stakedAmount = anInvestment.amount;

      // 6764% Fixed APY @ 0.1932876712% every 4 hours
      // > 0.1932876712 * 10000000000
      // 1932876712
      // > (((1932876712/2)/2)/2)
      // 241609589
      // > (((10000000000/2)/2)/2)
      // 1250000000
      stakedAmount += (stakedAmount * 241609589) / 1250000000;

      uint256 mintAmount = stakedAmount - anInvestment.amount;
      stakedToken.mint(anInvestment.investor, mintAmount);
      anInvestment.amount += mintAmount;

      // We're keeping this above auto-unlock bcoz
      // there might be an unlocking right after this
      if (anInvestment.unlocked == false) {
        totalLockedAmount += mintAmount; // Addition
      }

      InvestorStruct storage investor = investors[anInvestment.investor];

      // Auto-Unlock
      if (anInvestment.unlocked == false && block.timestamp >= anInvestment.unlockDate) {
        anInvestment.unlocked = true;
        totalLockedAmount -= anInvestment.amount; // Subtraction
        investor.unlockedAmount += anInvestment.amount;
      } else if (anInvestment.unlocked == true) {
        investor.unlockedAmount += mintAmount;
      }

      counter++;
      if (counter == 100) { // TODO: find number of accounts which is best for saving gas
        break;
      }
    }
  }

  // withdraw an unlocked amount, which means when unlocking date is due, 
  // `unlockedAmount` should be incremented for that account
  function withdraw(uint256 amount) external {
    require(allowWithdrawing == true, "Withdrawing has been halted");

    InvestorStruct storage investor = investors[msg.sender];

    require(
      amount <= investor.unlockedAmount && stakedToken.balanceOf(msg.sender) >= amount, 
      "Amount exceeds unlocked balance"
    );

    uint256 tempAmount = amount;

    // iterate and make deductions on unlocked investments until amount 
    // is reached... delete investment whose amount is fully utilized
    for (uint256 i = 0; i < investor.stakesIndices.length; i++) {
      InvestmentStruct storage anInvestment = investments[investor.stakesIndices[i]];
      if (anInvestment.unlocked == false) {
        continue;
      }
      if (anInvestment.amount > tempAmount) {
        anInvestment.amount -= tempAmount;
        break; // will always be our exit point
      } else if (tempAmount >= anInvestment.amount && anInvestment.amount > 0) {
        tempAmount -= anInvestment.amount;
        anInvestment.amount -= anInvestment.amount;
      }
    }

    // its a surety that tempAmount will be zero at this point

    investor.unlockedAmount -= amount;
    stakedToken.burnFrom(msg.sender, amount);

    // IMPORTANT: management must make sure there are 
    //            enough funds in contract always
    sikaToken.transfer(msg.sender, amount);
  }

  function deposit(uint256 amount) external {
    require(allowDepositing == true, "Depositing has been halted");

    // no need to check this again in disburseEarnings
    // bcoz withdawals can span multiple investments which
    // can definitely make some investments have their remainder
    // lower than the minimum deposit 
    require(amount >= minDeposit, "Amount is below the minimum deposit allowed");

    sikaToken.transferFrom(msg.sender, treasury, amount);

    InvestmentStruct memory anInvestment;
    anInvestment.unlockDate = block.timestamp + 30 days;
    anInvestment.amount = amount;
    anInvestment.investor = msg.sender;

    investments.push(anInvestment);

    InvestorStruct storage investor = investors[msg.sender];
    investor.stakesIndices.push(investments.length - 1);

    stakedToken.mint(msg.sender, amount);
    totalLockedAmount += amount;
  }

}
