// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./IAccounts.sol";
import "./IBurnableToken.sol";

// TODO: add check for zero address in relevant blocks

contract StakingPool {

  address treasury;

  IAccounts accountsContract;
  IBurnableToken stakedToken;
  IERC20 unstakedToken;

  bool allowStaking = true;
  bool allowUnstaking = true;

  uint256 public minStakeAmount;
  uint256 public totalLockedAmount;
  uint256 public issuedEarningsIndex;

  struct StakeStruct {
    address staker;
    uint256 startedAtEarningsIndex;
    uint16 lockingDays;
    uint256 amount;
    bool unlocked;
  }

  StakeStruct[] stakes;

  // Don't track empty indices since we're using a mapping 
  // which leaves no empty slots when deletions are performed
  struct StakerStruct {
    uint256 unlockedAmount;
    uint256[] stakesIndices;
  }

  mapping(address => StakerStruct) stakers; // staker => StakerStruct

  modifier onlyOwner {
    require(msg.sender == treasury, "You're not the owner");
    _;
  }

  constructor(address unstakedTokenAddress, address stakedTokenAddress) {
    unstakedToken = IERC20(unstakedTokenAddress);
    stakedToken = IBurnableToken(stakedTokenAddress);
    treasury = msg.sender;
  }

  // IMPORTANT!! This contract must be granted admin role in Accounts.sol
  //             in migration or test script via `Accounts.addAdmin`
  //             It serves to allow using Accounts.toggleIsExcludedFromTransferFee

  function setAccountsContract(address contractAddress) external onlyOwner {
    accountsContract = IAccounts(contractAddress);
    if (!accountsContract.isExcludedFromTransferFee(address(this))) {
      accountsContract.toggleIsExcludedFromTransferFee(address(this));
    }
  }

  function setMinStakeAmount(uint256 amount) external onlyOwner {
    minStakeAmount = amount;
  }

  function toggleAllowStaking() external onlyOwner {
    allowStaking = !allowStaking;
  }

  function toggleAllowUnstaking() external onlyOwner {
    allowUnstaking = !allowUnstaking;
  }

  function getUnlockedAmount() external view returns(uint256) {
    StakerStruct storage staker = stakers[msg.sender];
    return staker.unlockedAmount;
  }

  function getStakes() external view returns(StakeStruct[] memory) {
    StakerStruct storage staker = stakers[msg.sender];
    StakeStruct[] memory results = new StakeStruct[](staker.stakesIndices.length);

    uint256 j;
    for (uint256 i = 0; i < staker.stakesIndices.length; i++) {
      StakeStruct storage aStake = stakes[staker.stakesIndices[i]];
      results[j++] = aStake;
    }

    return results;
  }

  // Process
  // 1. call getStakesLength
  // 2. call disburseEarnings by supplying loop's starting index
  //    each call disburses earnings for ~100 accounts
  function getStakesLength() external onlyOwner view returns(uint256) {
    return stakes.length;
  }

  // let's but gas to send them their earnings
  // else if some just stakes and forgets to
  // apply then others will keep applying and
  // deplete the funds and the sleeper will get nothing
  // return number of stakes remaining to disburse earnings to
  function disburseEarnings(uint256 startLoopAtIndex) external onlyOwner {

    require(stakedToken.totalSupply() < 340000 ether, "Fund depleted!"); // 34% of stKDSK

    issuedEarningsIndex++;

    uint256 counter;

    // iterate over stakes
    // below this loop is a way for those with amount below minimum 
    // stake to unlock their tokens after the locking period
    for (uint256 i = startLoopAtIndex; i < stakes.length; i++) {
      // Using variable name `aStake` bcoz `stake` 
      // triggers an already declared variable name
      StakeStruct storage aStake = stakes[i];

      if (aStake.amount == 0 || aStake.amount < minStakeAmount) {
        continue; // non-existent key or amount below minimum stake
      }

      // IMPORTANT: Overflow may occur but value will 
      //            be truncated, hence no need to fix it.
      uint256 stakedAmount = aStake.amount;

      // how to derive dividers...
      // 1. pick any number sufficiently large e.g. 8
      // 2. divide that number by weekly percentage in `kdsk.html`
      // each APY x 0.66796 = next lesser APY
      // Compound Interest Formula: stakedAmount x (1 + (%/365))**(365*100)
      if (aStake.lockingDays == 91) {
        stakedAmount += (stakedAmount * 2) / 3168; // 25.9%
      } else if (aStake.lockingDays == 182) {
        stakedAmount += (stakedAmount * 7) / 7786; // 38.8%
      } else if (aStake.lockingDays == 364) {
        stakedAmount += (stakedAmount * 5) / 2912; // 87.0%
      }

      uint256 mintAmount = stakedAmount - aStake.amount;
      stakedToken.mint(aStake.staker, mintAmount);
      aStake.amount += mintAmount;

      // We're keeping this above auto-unlock bcoz
      // there might be an unlocking right after this
      if (aStake.unlocked == false) {
        totalLockedAmount += mintAmount; // Addition
      }

      StakerStruct storage staker = stakers[aStake.staker];

      // Auto-Unlock
      if (
        aStake.unlocked == false && 
        (issuedEarningsIndex - aStake.startedAtEarningsIndex) >= aStake.lockingDays
      ) {
        aStake.unlocked = true;
        totalLockedAmount -= aStake.amount; // Subtraction
        staker.unlockedAmount += aStake.amount;
      } else if (aStake.unlocked == true) {
        staker.unlockedAmount += mintAmount;
      }

      counter++;
      if (counter == 100) { // TODO: find number of accounts which is best for saving gas
        break;
      }
    }
  }

  // unstake an unlocked amount, which means when unlocking date is due, 
  // `unlockedAmount` should be incremented for that account
  function unstake(uint256 amount) external {
    require(allowUnstaking == true, "Unstaking has been halted");

    StakerStruct storage staker = stakers[msg.sender];

    require(
      amount <= staker.unlockedAmount && stakedToken.balanceOf(msg.sender) >= amount, 
      "Amount exceeds unlocked balance"
    );

    // iterate and make deductions on unlocked stakes until amount 
    // is reached... delete stake whose amount is fully utilized
    for (uint256 i = 0; i < staker.stakesIndices.length; i++) {
      StakeStruct storage aStake = stakes[staker.stakesIndices[i]];

      uint256 tempAmount = amount;
      if (aStake.unlocked == true) {
        if (aStake.amount > tempAmount) {
          aStake.amount -= tempAmount;
          break; // will always be our exit point
        } else if (tempAmount < amount && aStake.amount >= 0) {
          tempAmount -= aStake.amount;
        }
      }
    }

    staker.unlockedAmount -= amount;
    stakedToken.burnFrom(msg.sender, amount);

    // IMPORTANT: management must make sure there are 
    //            enough funds in contract always
    unstakedToken.transfer(msg.sender, amount);
  }

  function stake(uint16 lockingDays, uint256 amount) external {
    require(allowStaking == true, "Staking has been halted");

    // no need to check this again in disburseEarnings
    require(amount >= minStakeAmount, "Amount is below minimum stake");

    require(
      unstakedToken.balanceOf(msg.sender) >= amount, 
      "Amount provided exceeds balance"
    );

    // IMPORTANT: using require to perform actions 
    //            bcoz these expressions return boolean
    require(
      unstakedToken.transferFrom(msg.sender, treasury, amount), 
      "Failed to transfer staking amount"
    );

    StakeStruct memory aStake;
    aStake.startedAtEarningsIndex = issuedEarningsIndex;
    aStake.amount = amount;
    aStake.staker = msg.sender;

    if (lockingDays == 91 || lockingDays == 182 || lockingDays == 364) {
      aStake.lockingDays = lockingDays;
    } else {
      revert("Invalid parameter submitted");
    }

    stakes.push(aStake);

    StakerStruct storage staker = stakers[msg.sender];
    staker.stakesIndices.push(stakes.length - 1);

    stakedToken.mint(msg.sender, amount);
    totalLockedAmount += amount;
  }

}
